import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme, CssBaseline, Container } from '@mui/material';
import Navbar from './components/Navbar';
import PostList from './components/PostList';
import { Post } from './types';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#6C63FF',
      light: '#A5A2FF',
      dark: '#4A42D1',
    },
    secondary: {
      main: '#FF6584',
      light: '#FF8FA3',
      dark: '#D14A6A',
    },
    background: {
      default: '#F8F9FA',
      paper: '#FFFFFF',
    },
  },
  typography: {
    fontFamily: '"Noto Sans SC", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontSize: '2.5rem',
      fontWeight: 600,
    },
    h2: {
      fontSize: '2rem',
      fontWeight: 600,
    },
    h5: {
      fontSize: '1.25rem',
      fontWeight: 600,
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)',
          transition: 'transform 0.2s, box-shadow 0.2s',
          '&:hover': {
            transform: 'translateY(-4px)',
            boxShadow: '0 8px 12px rgba(0, 0, 0, 0.1)',
          },
        },
      },
    },
    MuiChip: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          backgroundColor: 'rgba(108, 99, 255, 0.1)',
          color: '#6C63FF',
          '&:hover': {
            backgroundColor: 'rgba(108, 99, 255, 0.2)',
          },
        },
      },
    },
  },
});

const mockPosts: Post[] = [
  {
    id: 1,
    title: 'React 入门指南',
    content: 'React 是一个用于构建用户界面的 JavaScript 库...',
    author: '张三',
    createdAt: '2024-03-20T10:00:00Z',
    tags: ['React', 'JavaScript', '前端'],
  },
  {
    id: 2,
    title: 'TypeScript 类型系统',
    content: 'TypeScript 的类型系统提供了强大的类型检查功能...',
    author: '李四',
    createdAt: '2024-03-21T14:30:00Z',
    tags: ['TypeScript', '编程语言'],
  },
];

const App: React.FC = () => {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Navbar />
        <Container maxWidth="lg" sx={{ mt: 4 }}>
          <Routes>
            <Route path="/" element={<PostList posts={mockPosts} />} />
            <Route path="/about" element={<div>关于页面</div>} />
          </Routes>
        </Container>
      </Router>
    </ThemeProvider>
  );
};

export default App;
