import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme, CssBaseline, Container } from '@mui/material';
import Navbar from './components/Navbar';
import PostList from './components/PostList';
import { Post } from './types';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#00BFA5', // 翠绿色
      light: '#1DE9B6',
      dark: '#00897B',
    },
    secondary: {
      main: '#006064', // 深青色
      light: '#00838F',
      dark: '#004D40',
    },
    background: {
      default: '#E8F5E9', // 淡绿色背景
      paper: '#FFFFFF',
    },
    text: {
      primary: '#1B5E20', // 深绿色
      secondary: '#2E7D32', // 中绿色
    },
  },
  typography: {
    fontFamily: '"Poppins", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontSize: '2.75rem',
      fontWeight: 600,
      letterSpacing: '-0.025em',
      lineHeight: 1.2,
    },
    h2: {
      fontSize: '2.25rem',
      fontWeight: 600,
      letterSpacing: '-0.025em',
      lineHeight: 1.2,
    },
    h5: {
      fontSize: '1.5rem',
      fontWeight: 600,
      letterSpacing: '-0.025em',
      lineHeight: 1.3,
    },
    subtitle1: {
      fontSize: '1.1rem',
      fontWeight: 500,
      letterSpacing: '0.00938em',
      lineHeight: 1.5,
    },
    body1: {
      fontSize: '1.1rem',
      lineHeight: 1.8,
      letterSpacing: '0.00938em',
    },
    button: {
      textTransform: 'none',
      fontWeight: 500,
      letterSpacing: '0.025em',
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 16,
          boxShadow: '0 4px 20px rgba(0, 191, 165, 0.08)',
          transition: 'transform 0.3s, box-shadow 0.3s',
          '&:hover': {
            transform: 'translateY(-4px)',
            boxShadow: '0 8px 24px rgba(0, 191, 165, 0.12)',
          },
        },
      },
    },
    MuiChip: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          backgroundColor: 'rgba(0, 191, 165, 0.1)',
          color: '#00BFA5',
          '&:hover': {
            backgroundColor: 'rgba(0, 191, 165, 0.15)',
          },
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          padding: '8px 24px',
        },
      },
    },
  },
});

const mockPosts: Post[] = [
  {
    id: 1,
    title: "The Art of Modern Web Development",
    content: "In the ever-evolving landscape of web development, staying ahead of the curve is crucial. Modern web development combines the power of React, TypeScript, and advanced CSS techniques to create seamless user experiences. This article explores the latest trends and best practices in the industry...",
    author: "Alexander Chen",
    createdAt: "2024-03-20T10:00:00Z",
    tags: ["Web Development", "React", "TypeScript"]
  },
  {
    id: 2,
    title: "Understanding TypeScript's Type System",
    content: "TypeScript's sophisticated type system is one of its most powerful features. It enables developers to catch errors during development and provides excellent tooling support. This deep dive into TypeScript's type system will help you understand its core concepts and advanced features...",
    author: "Sophia Williams",
    createdAt: "2024-03-21T14:30:00Z",
    tags: ["TypeScript", "Programming"]
  }
];

const App: React.FC = () => {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Navbar />
        <Container maxWidth="lg" sx={{ mt: 4 }}>
          <Routes>
            <Route path="/" element={<PostList posts={mockPosts} />} />
            <Route path="/about" element={<div>About</div>} />
          </Routes>
        </Container>
      </Router>
    </ThemeProvider>
  );
};

export default App;
