import React from 'react';
import { Card, CardContent, Typography, Box, Chip, Avatar } from '@mui/material';
import { Post } from '../types';
import { format } from 'date-fns';

interface PostListProps {
  posts: Post[];
}

const PostList: React.FC<PostListProps> = ({ posts }) => {
  return (
    <Box sx={{ 
      display: 'grid', 
      gridTemplateColumns: { xs: '1fr', sm: '1fr 1fr' },
      gap: 6,
      mt: 6,
      px: { xs: 2, sm: 4 }
    }}>
      {posts.map((post) => (
        <Card 
          key={post.id} 
          sx={{ 
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            backgroundColor: 'rgba(26, 26, 26, 0.8)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(0, 245, 255, 0.1)',
            transition: 'transform 0.3s, box-shadow 0.3s',
            '&:hover': {
              transform: 'translateY(-4px)',
              boxShadow: '0 8px 24px rgba(0, 245, 255, 0.15)',
            }
          }}
        >
          <CardContent sx={{ 
            flexGrow: 1, 
            p: 4,
            display: 'flex',
            flexDirection: 'column',
            gap: 3
          }}>
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: 2
            }}>
              <Avatar 
                src={`https://i.pravatar.cc/150?img=${post.id}`} 
                sx={{ 
                  width: 48, 
                  height: 48,
                  border: '2px solid rgba(0, 245, 255, 0.2)'
                }}
              />
              <Box>
                <Typography 
                  variant="subtitle1" 
                  sx={{ 
                    color: 'text.primary',
                    fontWeight: 600,
                    fontSize: '1.2rem',
                    mb: 0.5
                  }}
                >
                  {post.author}
                </Typography>
                <Typography 
                  variant="caption" 
                  sx={{ 
                    color: 'text.secondary',
                    fontSize: '0.85rem',
                    letterSpacing: '0.02em'
                  }}
                >
                  {format(new Date(post.createdAt), 'MMM d, yyyy')}
                </Typography>
              </Box>
            </Box>
            <Typography 
              variant="h6" 
              sx={{ 
                color: 'primary.main',
                fontWeight: 600,
                fontSize: '1.5rem',
                lineHeight: 1.3,
                letterSpacing: '-0.01em'
              }}
            >
              {post.title}
            </Typography>
            <Typography 
              variant="body1" 
              sx={{ 
                color: 'text.secondary',
                fontSize: '1.05rem',
                lineHeight: 1.7,
                letterSpacing: '0.01em'
              }}
            >
              {post.content.substring(0, 120)}...
            </Typography>
            <Box sx={{ 
              display: 'flex', 
              flexWrap: 'wrap', 
              gap: 1.5,
              mt: 'auto'
            }}>
              {post.tags.map((tag) => (
                <Chip
                  key={tag}
                  label={tag}
                  size="small"
                  sx={{
                    backgroundColor: 'rgba(0, 245, 255, 0.1)',
                    color: 'primary.main',
                    fontSize: '0.85rem',
                    height: 28,
                    '&:hover': {
                      backgroundColor: 'rgba(0, 245, 255, 0.2)',
                    }
                  }}
                />
              ))}
            </Box>
          </CardContent>
        </Card>
      ))}
    </Box>
  );
};

export default PostList;
