import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme, CssBaseline, Container } from '@mui/material';
import Navbar from './components/Navbar';
import PostList from './components/PostList';
import { Post } from './types';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#2D3748', // 深灰色
      light: '#4A5568',
      dark: '#1A202C',
    },
    secondary: {
      main: '#718096', // 中灰色
      light: '#A0AEC0',
      dark: '#4A5568',
    },
    background: {
      default: '#F7FAFC', // 非常淡的灰色背景
      paper: '#FFFFFF',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontSize: '2.5rem',
      fontWeight: 600,
      letterSpacing: '-0.025em',
    },
    h2: {
      fontSize: '2rem',
      fontWeight: 600,
      letterSpacing: '-0.025em',
    },
    h5: {
      fontSize: '1.25rem',
      fontWeight: 600,
      letterSpacing: '-0.025em',
    },
    subtitle1: {
      fontSize: '1rem',
      fontWeight: 500,
      letterSpacing: '0.00938em',
    },
    body1: {
      fontSize: '1rem',
      lineHeight: 1.75,
      letterSpacing: '0.00938em',
    },
    button: {
      textTransform: 'none',
      fontWeight: 500,
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)',
          transition: 'transform 0.2s, box-shadow 0.2s',
          '&:hover': {
            transform: 'translateY(-4px)',
            boxShadow: '0 8px 12px rgba(0, 0, 0, 0.1)',
          },
        },
      },
    },
    MuiChip: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          backgroundColor: 'rgba(45, 55, 72, 0.1)',
          color: '#2D3748',
          '&:hover': {
            backgroundColor: 'rgba(45, 55, 72, 0.2)',
          },
        },
      },
    },
  },
});

const mockPosts: Post[] = [
  {
    id: 1,
    title: "The Art of Modern Web Development",
    content: "In the ever-evolving landscape of web development, staying ahead of the curve is crucial. Modern web development combines the power of React, TypeScript, and advanced CSS techniques to create seamless user experiences. This article explores the latest trends and best practices in the industry...",
    author: "Alexander Chen",
    createdAt: "2024-03-20T10:00:00Z",
    tags: ["Web Development", "React", "TypeScript"]
  },
  {
    id: 2,
    title: "Understanding TypeScript's Type System",
    content: "TypeScript's sophisticated type system is one of its most powerful features. It enables developers to catch errors during development and provides excellent tooling support. This deep dive into TypeScript's type system will help you understand its core concepts and advanced features...",
    author: "Sophia Williams",
    createdAt: "2024-03-21T14:30:00Z",
    tags: ["TypeScript", "Programming"]
  }
];

const App: React.FC = () => {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Navbar />
        <Container maxWidth="lg" sx={{ mt: 4 }}>
          <Routes>
            <Route path="/" element={<PostList posts={mockPosts} />} />
            <Route path="/about" element={<div>About</div>} />
          </Routes>
        </Container>
      </Router>
    </ThemeProvider>
  );
};

export default App;
