import React, { useState } from 'react';
import {
  Box,
  TextField,
  InputAdornment,
  IconButton,
  Chip,
  Paper,
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import FilterListIcon from '@mui/icons-material/FilterList';

interface SearchBarProps {
  onSearch: (query: string) => void;
  onFilter: (tag: string) => void;
  tags: string[];
  activeTag: string | null;
}

const SearchBar: React.FC<SearchBarProps> = ({ onSearch, onFilter, tags, activeTag }) => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = () => {
    onSearch(searchQuery);
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <Paper
      elevation={0}
      sx={{
        p: 2,
        mb: 4,
        backgroundColor: 'background.paper',
        border: '1px solid rgba(0, 245, 255, 0.1)',
      }}
    >
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Search articles..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyPress={handleKeyPress}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon sx={{ color: 'text.secondary' }} />
              </InputAdornment>
            ),
            endAdornment: (
              <InputAdornment position="end">
                <IconButton onClick={handleSearch}>
                  <FilterListIcon sx={{ color: 'text.secondary' }} />
                </IconButton>
              </InputAdornment>
            ),
          }}
          sx={{
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                borderColor: 'rgba(0, 245, 255, 0.2)',
              },
              '&:hover fieldset': {
                borderColor: 'rgba(0, 245, 255, 0.4)',
              },
              '&.Mui-focused fieldset': {
                borderColor: 'primary.main',
              },
            },
          }}
        />
      </Box>

      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
        {tags.map((tag) => (
          <Chip
            key={tag}
            label={tag}
            onClick={() => onFilter(tag)}
            sx={{
              backgroundColor: activeTag === tag ? 'primary.main' : 'rgba(0, 245, 255, 0.1)',
              color: activeTag === tag ? 'background.paper' : 'primary.main',
              '&:hover': {
                backgroundColor: activeTag === tag ? 'primary.dark' : 'rgba(0, 245, 255, 0.2)',
              },
            }}
          />
        ))}
      </Box>
    </Paper>
  );
};

export default SearchBar;
